/*
 * fats init command
 * */
exports.name = 'init';
exports.usage = '[options]';
exports.desc = 'initialization project by template';
exports.register = function (commander) {

	commander
		.option('-b, --branches <switch>', 'include trunk and branches directory', 'off')
		.option('-t, --template <template>', 'select template when project init', 'fats-default')
		.action(function (cli) {
			var hasBranches = cli.branches;
			var template = cli.template;
			var path = process.cwd();

			console.log('hasBranches: ' + hasBranches);
			console.log('template: ' + template);
			console.log('path: ' + path);

		}).on('--help', function(){
			console.log('  Examples:');
			console.log('');
			console.log('    $ custom-help --help');
			console.log('    $ custom-help -h');
			console.log('');
		});
};