var Config = module.exports = function () {
};

var util = require('./util');
var defaultCfg = Config.defaultCfg = {};

Config.extend = function (obj) {
	defaultCfg = util.extend(defaultCfg, obj);
};

Config.set = function (key, value) {
	defaultCfg[key] = value;
};

Config.del = function (key) {
	delete defaultCfg[key];
};

Config.get = function () {
	if (arguments.length > 0) {
		return defaultCfg[arguments[0]];
	} else {
		return defaultCfg;
	}
};
